"use strict";

function setShadow(e) {
    var boxOffset = $this.offset(), boxX = boxOffset.left + boxWidth / 2, boxY = boxOffset.top + boxHeight / 2, mouseX = e.pageX, mouseY = e.pageY, shadowX = -1 * (mouseX - boxX) / 10, shadowY = -1 * (mouseY - boxY) / 10, shadowZ = Math.sqrt(Math.pow(shadowX, 2) + Math.pow(shadowY, 2)) / 1.5;
    $("body").css("text-shadow", shadowX + "px " + shadowY + "px " + shadowZ + "px rgba(0,0,0,.5");
}

var $this = $(".megatron"), boxWidth = $this.width(), boxHeight = $this.height(), boxOffset, boxX, boxY, mouseX, mouseY, shadowX, shadowY, shadowZ;

!function() {
    // Debugging
    // $('#cursorPosition').html("Cursor X Coordinate : " + mouseX + "; Y Coordinate : " + mouseY);
    // $('#boxPosition').html("Box X Coordinate : " + boxX + "; Y Coordinate : " + boxY);
    // $('#shadowNumbers').html("X: " + shadowX + "; Y: " + shadowY + " Z: " + shadowZ);
    $(document).on("mousemove", setShadow).on("resize", setShadow);
}();